import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialAuthState = { isAuthenticated: false };

const authSlice = createSlice({
  name: "authentication",
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  },
});

const store = configureStore({
  // reducer: counterSlice.reducer, // This is the usage when we have one reducer
  reducer: { counter: counterSlice.reducer, auth: authSlice.reducer }, // This is the usage when we have multiple reducers, in the background the configureStore will merge all those reducers into one main/big reducer
});

export const authActions = authSlice.actions;

export default store;
