import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialCounterState = { counter: 0, showCounter: true };

const counterSlice = createSlice({
  name: "counter",
  initialState: initialCounterState,
  reducers: {
    // Each reducer will have his own unique identifier as a type
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter++;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload;
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

const initialAuthState = { isAuthenticated: false };

const authSlice = createSlice({
  name: "authentication",
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  },
});

const store = configureStore({
  // reducer: counterSlice.reducer, // This is the usage when we have one reducer
  reducer: { counter: counterSlice.reducer, auth: auth }, // This is the usage when we have multiple reducers, in the background the configureStore will merge all those reducers into one big reducer
});

export const counterActions = counterSlice.actions;

export default store;
